plugins {
    `java-library`
    `maven-publish`
}

repositories {
    mavenCentral()
}

dependencies {
    implementation("org.java-websocket:Java-WebSocket:1.6.0")
    implementation("com.google.protobuf:protobuf-java:4.29.1")
    implementation("org.slf4j:slf4j-api:2.0.17")
    implementation("org.slf4j:slf4j-nop:2.0.17")
}

sourceSets {
    main {
        java {
            setSrcDirs(listOf("HLA1516-2025/src"))
        }
    }
}

java {
    withSourcesJar()
}

publishing {
    publications {
        create<MavenPublication>("mavenJava") {
            from(components["java"])

            groupId = "se.pitch.oss.fedpro"        // your group
            artifactId = "hla4-api"   // your artifact name
            version = "1.0.0"              // your version
        }
    }
    repositories {
        mavenLocal()    // Publishes to local Maven cache (~/.m2/repository)
    }
}

version = "1.0.0"
