 * For more details take a look at the 'Building Java & JVM projects' chapter in the Gradle
 * User Manual available at https://docs.gradle.org/7.4.1/userguide/building_java_projects.html
 */

plugins {
    // Apply the application plugin to add support for building a CLI application in Java.
    `java-library`
    `maven-publish`
    
}

repositories {
    mavenCentral()
}

dependencies {
    implementation("org.java-websocket:Java-WebSocket:1.6.0")
    implementation("com.google.protobuf:protobuf-java:4.29.1")
    implementation("org.slf4j:slf4j-api:2.0.17")
    implementation("org.slf4j:slf4j-nop:2.0.17")
    implementation("net.jcip:jcip-annotations:1.0")
    implementation(project(":java:api"))
    implementation(project(":java:protobuf"))
    implementation(project(":java:session"))
    implementation(project(":java:client_common"))
}

sourceSets {
    main {
        java {
            setSrcDirs(listOf("src","gensrc"))
        }
    }
}

tasks.jar {
    from(file("resources/META-INF/services")) {
        into("META-INF/services")
    }
}

publishing {
    publications {
        create<MavenPublication>("mavenJava") {
            from(components["java"])

            groupId = "se.pitch.oss.fedpro"
            artifactId = "client"
            version = "1.0.0"
        }
    }
    repositories {
        mavenLocal()
    }
}

version = "1.0.0"
